int choose_difficulty()
{
    system("CLS");
    cout<<"\nChoose the game difficulty\nKeep in mind:\n";color(2);
    cout<<"\t\t - Easy: means more lives and easier words\n";color(5);
    cout<<"\t\t - Normal: means less lives and moderate words\n";color(4);
    cout<<"\t\t - Hard: means even less lives and really short hard words\n";color(7);
    cout<<"Note: you get more points if you win the game in harder modes";
    cout<<"\n\nHow difficult would you like the game?\n"<<endl;color(2);
    cout<<"\t1- Easy   = 1 point  + 3 hints (enter 1)\n";color(5);
    cout<<"\t2- Normal = 2 points + 2 hints (enter 2)\n";color(4);
    cout<<"\t3- Hard   = 3 points + 1 hint  (enter 3)\n";color(7);

    cout<<"\nEnter your choice: ";

    short int dif;
    cin>>dif;
    while (dif>3||dif<1||!cin)
    {
        sound("Referee Whistle.wav");
        cin.clear();
        cin.ignore();color(207);
        cout<<"enter 1, 2 or 3 only";color(7);
        cout<<"\nEnter your choice: ";
        cin>>dif;
    }
    return dif;

}

char input_alphabitical_char(bool is_this_letter_used[])
{
    cout<<"\nEnter a character: ";
    string str;
    cin>>str;
    if(str=="0")return '0';
    if(str=="1")return '1';
    while(str.size()!=1 || !isalpha(str[0]) || is_this_letter_used[getindex(str[0])])
    {
        sound("Referee Whistle.wav");
        cout<<endl<<endl;
        color(207);
        if(str.size()!=1){cout<<"You have entered more than a letter, please enter only one English letter.";color(7);cout<<endl;}
        else if(!isalpha(str[0])){cout<<"You have entered an invalid value, please enter only one English letter.";color(7);cout<<endl;}
        else if(is_this_letter_used[getindex(str[0])]){cout<<"You have used this letter before, try again.";color(7);cout<<endl;}
        cout<<"\nEnter a character: ";
        cin>>str;
        if(str=="0")return '0';
        if(str=="1")return '1';
    }
    str[0]=toupper(str[0]);
    return str[0];
}

